import React from 'react';
import styled from 'styled-components';
import { PageTitle } from '../pages/podcasts/_elements';

const Links = styled.section`
  margin: auto;
  display: flex;
  justify-content: center;
  max-width: 100%;
  margin-top: 32px;
  color: #999999;
  flex-wrap: wrap;

  > a:not(:last-child) {
    margin-right: 24px;
  }

  @media screen and (max-width: 650px) {
    grid-template-columns: 1fr 1fr;
    justify-content: center;
  }

  @media screen and (max-width: 360px) {
    grid-template-columns: 1fr;
    justify-content: center;
  }

  a {
    color: #999999;
    display: flex;
    align-items: center;
    text-decoration: none;
    transition: 100ms ease in;
    margin-bottom: 24px;

    :hover {
      color: white;
    }
  }

  svg {
    margin-right: 16px;
  }
`;

const PodcastLinks = ({ apple, google, spotify, name, tuneIn, stitcher }) => (
  <div
    css={`
      margin-top: 72px;
      margin-bottom: 200px;
    `}
  >
    <PageTitle
      as="h4"
      css={`
        font-size: 32px;
        text-align: center;
        line-height: 1;
        font-weight: 900;
      `}
    >
      Follow {name}
    </PageTitle>
    <Links>
      <a href={apple} target="_blank" rel="noreferrer">
        <Apple />
        Apple Podcasts
      </a>
      <a href={google} target="_blank" rel="noreferrer">
        <Google />
        Google Podcasts
      </a>
      <a href={spotify} target="_blank" rel="noreferrer">
        <Spotify /> Spotify
      </a>
      <a href={stitcher} target="_blank" rel="noreferrer">
        <Stitcher />
        Stitcher
      </a>
      <a href={tuneIn} target="_blank" rel="noreferrer">
        <TuneIn />
        TuneIn
      </a>
    </Links>
  </div>
);

export default PodcastLinks;

