            "category": "Emmet"
        }, {
            "command": "editor.emmet.action.reflectCSSValue",
            "title": "%command.reflectCSSValue%",
            "category": "Emmet"
        }],
        "menus": {
            "commandPalette": [{
                "command": "editor.emmet.action.wrapIndividualLinesWithAbbreviation",
