      "end": "(?=.)",
      "applyEndPatternLast": 1,
      "captures": {
        "1": { "name": "keyword.type.graphql"},
        "2": { "name": "keyword.type.graphql"},
        "3": { "name": "keyword.interface.graphql"},
        "4": { "name": "keyword.input.graphql"},
        "5": { "name": "support.type.graphql"}
      },
      "patterns": [
        {
          "begin": "\\s*\\b(implements)\\b\\s*",
