          "beginCaptures": {
            "1": { "name": "keyword.implements.graphql" }
          },
          "patterns": [
            {
              "match": "\\s*([_A-Za-z][_0-9A-Za-z]*)",
              "captures": {
                "1": { "name": "support.type.graphql" }
              }
            },
            { "include": "#graphql-directive" },
            { "include": "#graphql-ampersand" },
            { "include": "#graphql-comma" }
